import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { addToCart } from "../actions/cartAction";

const CartScreen = (props) => {
  const cart = useSelector((state) => state.cart);
  const { cartItems } = cart;
  const productId = props.match.params.id;
  const qty = props.location.serach
    ? Number(props.loaction.serach.split("=")[1])
    : 1;

  const dispatch = useDispatch();

  console.log(cart);

  useEffect(() => {
    console.log(productId)
    console.log(qty)
    if (productId) {
      dispatch(addToCart(productId, qty));
    }
  }, [dispatch, productId, qty]);

  return (
    <div className= "cart">
      <div className= "cart-list"></div>
      <ul className= "cart-list-container">
        <li>
          <h3>Shopping Cart</h3>
          <div>Price</div>
        </li>
        {cartItems.length === 0 ? (
          <div> Cart is empty</div>
        ) : (
          cartItems.map((item) => (
            <div key="cart.item">
              <img src={item.image} alt="product" />
              <div className="cart-name">
                <div > {item.name}</div>
              </div>
              <div>
                Qty:
                <select>
                  <options value="1">1</options>
                  <options value="2">2</options>
                  <options value="3">3</options>
                </select>
              </div>
              <div>{item.price}</div>
              <div className="cart-action">
               <h3>Subtotal ({cartItems.reduce((a,c)=> a+c.qty, 0)}items) </h3>:${cartItems.reduce((a,c) =>a +c.qty * c.qty, 0)}
              </div>
            </div>
          ))
        )}
      </ul>
    </div>
  );
};

export { CartScreen };
