import React, { useDispatch, useEffect, } from "react";
import {useSelector} from "redux"
import "./homepage.css";
import { Link } from "react-router-dom";
import { listProducts } from "./../actions/productActions";

const Homescreen = (props) => {
  const  productList = useSelector((state) => state.productList);
  const { loading, error, products } = productList;
  const dispatch = useDispatch();
console.log(productList)
  useEffect(() => {
    dispatch(listProducts());
  }, []);

  return (
    <div>
      <ul className="products">
        {products &&
          products.length !== 0 &&
          products.map((product) => (
            <li key={product.name} className="products-li">
              <div>
                <Link to={"/product/" + product._id}>
                  <img
                    className="product-image"
                    src={product.imageUrl}
                    alt=""
                  />
                </Link>
              </div>
              <div className="product-name">
                <Link to={"/product/" + product._id}>{product.name} </Link>
              </div>
              <div className="product-brand">{product.brand}</div>
              <div className="product-prices">${product.price}</div>
              <div className="product-rating">
                {product.rating} stars of (10)
              </div>
              <div className="product-reveiw">{product.numReveiw} </div>
            </li>
          ))}
      </ul>
    </div>
  );
};

export default Homescreen;
