import React, {useState, useEffect}from "react";
import "./homepage.css";
import { Link } from "react-router-dom";
// import data from "./../data";
import axios from  "axios"

const Homescreen = (props) => {
  const [products, setproducts] = useState("");
  useEffect(() => {
    const fetchData = async ()=> {
      const data = await axios.get("/api/products");
      setproducts(data)
    }
    fetchData ()
    return () => {
     
    }
  },[])
  return (
    <div>
      <ul className="products">
        {products && products.length === 0 && products.map((product) => (
          <li key="product" className="products-li">
            <div>
              <Link to={"/product/" + product._id}>
                <img className="product-image" src={product.imageUrl} alt="" />
              </Link>
            </div>
            <div className="product-name">
              <Link to={"/product/" + product._id}>{product.name}</Link>
            </div>
            <div className="product-brand">{product.brand}</div>
            <div className="product-prices">${product.price}</div>
            <div className="product-rating">{product.rating} stars of (10)</div>
            <div className="product-reveiw">{product.numReveiw} </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Homescreen;
