import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { addToCart, removeFromCart } from "../actions/cartAction";
import MessageBox  from "../components/MessageBox";



const CartScreen = (props) => {
  const cart = useSelector((state) => state.cart);
  const { cartItems } = cart;
  const productId = props.match.params.id;
  const qty = props.location.search
    ? Number(props.location.search.split("=")[1])
    : 1;
  const dispatch = useDispatch();


  const removeFromCartHandler = (productId) =>{
    dispatch(removeFromCart(productId))
  }
  const checkOutHandler = () => {
    props.history.push("signin?redirect=shipping");
  }

  

  useEffect(() => {
    if (productId) {
      dispatch(addToCart(productId, qty));
    }
  }, [dispatch, productId, qty]);

  return (
    <div className="row top">
       <div className="col-2">
         <h1>Shopping Cart</h1>
         {cartItems.length === 0 ? <MessageBox>
            Cart is empty. <Link to="/">
              Go Shopping
            </Link>
         </MessageBox>:
         (
           <ul>
              {cartItems?.map(item => (
                
                  <li key= {item.product}>
                  
                      <div className="row">
                          <div>
                              <img src={item.image} alt={item.product} className="small"/>
                          </div>
                          <div classNAme="min-30">
                             <Link  className="link-item"to={`/product/${item.product}`}>{item.name}</Link> 
                          </div>
                          <div>
                            <select value={item.qty} onchange={e => dispatch(addToCart(item.product), Number(e.target.value))}>
                                {[...Array(item.countInStock).keys()].map( x=> (
                                      <option key={x=1} value={x+1}>{x+1}</option>
                                    )
                                  )}
                            </select>
                          </div>
                          <div>
                              ${item.price}
                          </div>
                          <div>
                                  <button type="button" onClick={()=> removeFromCartHandler(item.product)}>Delete</button>
                          </div>
                      </div>
                  </li>
              ))}
           </ul>
         )
         }
       </div>
    </div>
  );
};

export { CartScreen };
